      if (user!= null) {
        // User is authenticated, proceed with Firebase services
      }
    });

    // Sign out of Firebase
    firebaseAuth.onDisconnect.listen((error) {
      if (error != null) {
        // Handle error
      }
    });
  } catch (e) {
    // Handle other errors
  }
}
