   In your Flutter app, create a widget for the Firebase Authentication screen. This widget will handle the sign-in/sign-out logic:

```dart
import 'package:firebase_auth/firebase_auth.dart';

class FirebaseSignIn extends StatefulWidget {
  @override
  _FirebaseSignInState createState() => _FirebaseSignInState();
}

class _FirebaseSignInState extends State<FirebaseSignIn> {
  final _controller = TextEditingController();
  bool _isLoggingIn = true;

  void _handleSignInButtonClick() {
    if (_isLoggingIn) {
      setState(() {
        _isLoggingIn = false;
      });
    }

    String email = _controller.text;
    String password = _controller.text;

    firebaseAuth = FirebaseAuth.instance;
    firebaseAuth.authStateChanges().listen((snapshot) {
      if (snapshot.status == firebaseAuthStatus.normal) {
        firebaseAuth.authCredential = firebaseAuth.Credential.password;
        firebaseAuth.authVerifyAsync(email, password).then((success) {
          if (success) {
            setState(() {
              _isLoggingIn = false;
              _handleSignInButtonClick();
            });
          }
        });
      } else if (snapshot.status == firebaseAuthStatus.permanent) {
        // Handle permanent error
      }
    });
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Sign in with Firebase'),
      ),
      body: Padding(
        padding: EdgeInsets.all(16.0),
        child: Column(
          children: [
            TextFormField(
              controller: _controller,
              decoration: InputDecoration(labelText: 'Email'),
              validator: (value) {
                if (value.isEmpty) {
                  return "Please enter your email address.";
                }
                return null;
              },
              errorText: (error) {
                return error is firebaseAuthException.emailRequired ? "Email is required." : "";
              },
              helperText: "Email address is required.",
            ),
            TextFormField(
              controller: _controller,
              decoration: InputDecoration(labelText: 'Password'),
              validator: (value) {
                if (value.isEmpty) {
                  return "Please enter your password";
                }
                return null;
              },
              errorText: (error) {
                return error is firebaseAuthException.passwordRequired ? "Password is required." : "";
              },
              helperText: "Password is required.",
            ),
            ElevatedButton(
              onPressed: _isLoggingIn ? _handleSignInButtonClick : null,
              child: Text(_isLoggingIn ? "Sign In" : "Sign Up"),
            ),
          ],
        ),
      ),
    );
  }
}
